import React from "react";
import { MessageSquare } from "lucide-react";

const NoChatSelected = () => {
  return (
    <div className="flex items-center justify-center p-8 w-full h-full bg-base-100/50">
      <div className="max-w-md w-full bg-base-200 rounded-lg shadow-lg p-6 flex flex-col items-center">
        {/* Icon */}
        <div className="w-12 h-12 bg-primary rounded-full flex items-center justify-center shadow-md">
          <MessageSquare className="w-6 h-6 text-base-100" />
        </div>

        {/* Text */}
        <h2 className="text-lg font-semibold text-gray-800 mt-4">
          Select a Chat to Start Messaging
        </h2>
        <p className="text-sm text-gray-500 mt-1 text-center">
          Please choose a conversation from the sidebar to begin.
        </p>
      </div>
    </div>
  );
};

export default NoChatSelected;
