import React, {useState} from 'react'
import { useAuthStore } from '../store/useAuthStore';
import { MessageSquare , Mail} from 'lucide-react';


const SignUpPage = () => {
    const [showPassword, setShowPasqword] = useState(false);
    const [formData, setFormData] = useState({
        email: '',
        password: '',
    });
    const {signup, isSignUp} = useAuthStore();
    const validateForm = () => {
        return formData.email.length > 0 && formData.password.length > 0;
    }
    const handleSubmit = (e) => {
        e.preventDefault();
        signup(formData);
    }
  return (
    <div className = "min-h-screen grid lg:grid-cols-2"> 
    {/* left side of form */}
    <div className = "flex flex-col justify-center items-center p-6 sm:p-12">
        <div className = "w-full max-w-md" space-y-8>
            {/*Logo*/}
            <div className = "flex flex-col items-center gap-2 group">
                <div className = "size-12 rounded-xl bg-primary/10 flex items-center justify-center group-hover:bg-primary/20 transition-colors">
                    <MessageSquare className = "size-10 text-primary" />

                </div>
                <h1 className = "text-3xl font-bold text-primary">Create Account </h1>
                <p className = "text-sm text-primary/60">Get started with our service</p>
            </div>
            </div>
            <form onSubmit={handleSubmit} className="space-y-6">
                <div className = "form-control">
                    <label className = "label">
                        <span className = "label-text">Email</span>


                    </label>
                    <div className = "relative">
                        <div className = "absolute inset-y-0 left-0 flex items-center pl-3 flex items-center pointer-events-none">
                            <Mail className="size-5 text-base-content/40"/>

                        </div>
                        <input type="email" className="input" placeholder="Email" value={formData.email} onChange={(e) => setFormData({...formData, email: e.target.value})} />


                </div>
                </div>
            </form>
    </div>    
    </div>
  )
}

export default SignUpPage;
