import React from "react";
import { MessageSquare } from "lucide-react";

const NoChatSelected = () => {
  return (
    <div className="w-full h-full flex items-center justify-center bg-base-100/50 p-8">
      <div className="max-w-lg w-full bg-base-200 rounded-lg shadow-lg p-8 space-y-6 text-center">
        {/* Icon Section */}
        <div className="flex justify-center">
          <div className="w-16 h-16 bg-primary rounded-full flex items-center justify-center shadow-md">
            <MessageSquare className="w-8 h-8 text-base-100" />
          </div>
        </div>

        {/* Text Section */}
        <div>
          <h2 className="text-xl font-semibold text-gray-800">
            Select a Chat to Start Messaging
          </h2>
          <p className="text-sm text-gray-500 mt-2">
            Please choose a conversation from the sidebar to begin.
          </p>
        </div>
      </div>
    </div>
  );
};

export default NoChatSelected;
