import React from "react";
import { MessageSquare } from "lucide-react";

const NoChatSelected = () => {
  return (
    <div className="w-full flex flex-1 flex-col items-center justify-center p-16 bg-base-100/50">
      <div className="max-w-md text-center space-y-6">
        {/* Icon Section */}
        <div className="flex justify-center gap-4 mb-4">
          <div className="relative">
            <div className="w-20 h-20 bg-primary rounded-full flex items-center justify-center animate-bounce shadow-lg">
              <MessageSquare className="w-10 h-10 text-base-100" />
            </div>
          </div>
        </div>

        {/* Text Section */}
        <div className="space-y-2">
          <h2 className="text-2xl font-bold text-primary">
            Select a Chat to Start Messaging
          </h2>
          <p className="text-sm text-gray-500">
            Choose a conversation from the sidebar to view and send messages.
          </p>
        </div>
      </div>
    </div>
  );
};

export default NoChatSelected;
